import express, { Request, Response } from 'express';
import allRoutes from "./routes/allRoutes"
import cors from 'cors';
import dotenv from 'dotenv';
import swaggerSetup from '../swaggerConfig';
import './database/config/database'

const app = express();
app.use(express.json());
app.use(cors());
dotenv.config();

app.use("/api", allRoutes);

swaggerSetup(app);

const PORT = process.env.PORT || 5050;
app.listen(PORT, () => {
    console.log(`Server is running on port ${PORT}`);
});

export default app;

import express from 'express';

import todoUserRoute from './todouserRoutes';

const router = express.Router();

router.use("/todousers", todoUserRoute);

export default router;

import jwt from 'jsonwebtoken';
import express from 'express';

const JWT_KEY = process.env.JWT_KEY || 'SECRET';

export const authentication = (req: express.Request, res: express.Response, next: express.NextFunction) => {
    const authHeader = req.headers['authorization'];
    const token = authHeader && authHeader.split(' ')[1];

    if (!token) {
        return res.json({status: false, message: "You are not logged in."});
    }

    jwt.verify(token, JWT_KEY, (err, decoded) => {
        if (err) {
            return res.json({status: false, message: "You are not logged in. Invalid Token."});
        }
        (req as any).userId = (decoded as any).userId;
        next();
    });
};

import express from 'express';
import { comparePassword, encryptPassword, generateToken } from '../../../helpers'
import { createUser, deleteUserById, findUserByEmail, findUserById, getAllUsers, editUserByUsername } from '../repository/todouserRepo';


// Login
export const login = async (req: express.Request, res: express.Response) => {
    try {
        const { email, password } = req.body;

        const user = await findUserByEmail(email);
        if (!user) {
            return res.status(404).json({
                status: 'fail',
                message: 'No user found with the provided email',
            });
        }

        const isPasswordMatch = await comparePassword(password, user.password);
        if (!isPasswordMatch) {
            return res.status(404).json({
                status: 'fail',
                message: 'Incorrect password',
            });
        }

        const token = generateToken(user._id);

        return res.status(200).json({
            status: 'success',
            data: {
                user,
                token,
            },
        });
    } catch (error: any) {
        console.log(error);
        return res.status(500).json({
            message: error.message,
            code: error.code,
        });
    }
};

// Signup
export const signup = async (req: express.Request, res: express.Response) => {
    try {
        const { username, email, password } = req.body;

        if (!username || !email || !password) {
            return res.status(404).json({
                status: 'fail',
                message: 'insert user credentials',
            });
        }

        const existingUser = await findUserByEmail(email);
        if (existingUser) {
            return res.status(404).json({
                status: 'fail',
                message: 'User already exists',
            });
        }

        const hashedPassword = await encryptPassword(password);
        const newUser = await createUser({
            username,
            email,
            password: hashedPassword,
        });

        return res.status(200).json({
            status: 'success',
            data: newUser,
        });
    } catch (error: any) {
        console.log(error);
        return res.status(500).json({
            message: error.message,
            code: error.code,
        });
    }
};

// View all users
export const viewUsers = async (req: express.Request, res: express.Response) => {
    try {
        const allUsers = await getAllUsers();
        return res.status(200).json({
            message: 'All users retrieved successfully',
            data: allUsers,
        });
    } catch (error: any) {
        console.log(error);
        return res.status(500).json({
            message: error.message,
            code: error.code,
        });
    }
};

// Get a single user
export const singleUser = async (req: express.Request, res: express.Response) => {
    try {
        const userId = req.params.id;
        const user = await findUserById(userId);
        if (!user) {
            return res.status(404).json({
                message: 'User not found',
            });
        }

        return res.status(200).json({
            message: 'User retrieved successfully',
            data: user,
        });
    } catch (error: any) {
        console.log(error);
        return res.status(500).json({
            message: error.message,
            code: error.code,
        });
    }
};

// Edit user
export const editUser = async (req: express.Request, res: express.Response) => {
    try {
        const userId = req.params.id;
        const { username } = req.body;

        const updatedUser = await editUserByUsername(userId, username);

        if (!updatedUser) {
            return res.status(404).json({
                message: `User with ${userId}} is not found.`,
            });
        }

        return res.status(200).json({
            message: 'User edited successfully',
            data: updatedUser,
        });
    } catch (error: any) {
        console.error(error);
        return res.status(500).json({
            message: error.message,
            code: error.code,
        });
    }
};

// Delete user
export const deleteUser = async (req: express.Request, res: express.Response) => {
    try {
        const userId = req.params.id;
        const existingUser = await findUserById(userId);

        if (!existingUser) {
            return res.status(404).json({
                message: 'User not found',
            });
        }

        const deletedUser = await deleteUserById(userId);

        return res.status(200).json({
            message: 'User deleted successfully',
            data: deletedUser,
        });
    } catch (error: any) {
        console.error(error);
        return res.status(500).json({
            message: error.message,
            code: error.code,
        });
    }
};

import jwt from 'jsonwebtoken'
import { encryptPassword, comparePassword } from './passwordhelpers';

const JWT_KEY = process.env.JWT_KEY || 'SECRET';

const generateToken = (userId: any): any => {
    const token = jwt.sign({userId}, JWT_KEY, {expiresIn: '12h'});
    return token;
}

export { generateToken, encryptPassword, comparePassword }

import bcrypt from 'bcrypt';

const encryptPassword = async (password: string) =>{
    return await bcrypt.hash(password, 10);
}

const comparePassword = async (password: string, hashedPassword: string) =>{
    return await bcrypt.compare(password, hashedPassword);
}

export { encryptPassword, comparePassword }

import User from "../../../database/models/todouser";


const createUser = async (body: any) => {
    return await User.create(body);
}

const getAllUsers = async () => {
    return await User.find()
}

const findUserByEmail = async (email: string) => {
    return await User.findOne({email});
}

const findUserById = async (id: string) => {
    return await User.findOne({_id: id});
}

const deleteUserById = async (id: string) => {
    return await User.deleteOne({_id: id});
}

const editUserByUsername = async (id: string, username: string) => {
    return await User.findByIdAndUpdate({_id: id},{username})
}

export {
    createUser,
    findUserByEmail,
    deleteUserById,
    editUserByUsername,
    getAllUsers,
    findUserById
}
